import { InjectedConnector } from "@wagmi/core/connectors/injected";
import { EVMWallet, EVMWalletConfig } from "./evm";

export enum InjectedWallets {
  MetaMask = "MetaMask",
  BraveWallet = "Brave Wallet",
  KuCoinWallet = "KuCoin Wallet",
  CoinbaseWallet = "Coinbase Wallet",
  FrontierWallet = "Frontier Wallet",

  Generic = "Injected Wallet",
}

interface InjectedWalletOptions {
  name?: string | ((detectedName: string | string[]) => string);
}

export type InjectedWalletConfig = EVMWalletConfig<InjectedWalletOptions>;

export class InjectedWallet extends EVMWallet<
  InjectedConnector,
  InjectedWalletOptions
> {
  constructor(config: InjectedWalletConfig = {}) {
    super(config);
  }

  createConnector(): InjectedConnector {
    return new InjectedConnector({
      chains: this.chains,
      options: this.connectorOptions,
    });
  }

  getName(): string {
    const name = this.connector.name;
    return name === "Injected" ? InjectedWallets.Generic : name;
  }

  getUrl(): string {
    switch (this.getName()) {
      case InjectedWallets.MetaMask:
        return "https://metamask.io/";
      case InjectedWallets.BraveWallet:
        return "https://brave.com/";
      case InjectedWallets.KuCoinWallet:
        return "https://kuwallet.com/";
      case InjectedWallets.FrontierWallet:
        return "https://www.frontier.xyz/";
      default:
        return "https://ethereum.org/";
    }
  }

  getIcon(): string {
    switch (this.getName()) {
      case InjectedWallets.MetaMask:
        return "data:image/svg+xml;base64,";
      case InjectedWallets.BraveWallet:
        return "data:image/svg+xml;base64,";
      case InjectedWallets.KuCoinWallet:
        return "data:image/png;base64,";
      case InjectedWallets.FrontierWallet:
        return "data:image/svg+xml;base64,";
      default:
        return "data:image/svg+xml;base64,";
    }
  }
}
