import { InjectedConnector } from "@wagmi/core/connectors/injected";
import { EVMWallet, EVMWalletConfig, EVMWalletType } from "./evm";

export enum InjectedWallets {
  MetaMask = "MetaMask",
  BraveWallet = "Brave Wallet",
  KuCoinWallet = "KuCoin Wallet",
  CoinbaseWallet = "Coinbase Wallet",
  FrontierWallet = "Frontier Wallet",
  BitKeepWallet = "BitKeep",
  OKXWallet = "OKX Wallet",

  Generic = "Injected Wallet",
}

const GENERIC_ICON =
  "data:image/svg+xml;base64,";

interface InjectedWalletOptions {
  name?: string | ((detectedName: string | string[]) => string);
}

export type InjectedWalletConfig = EVMWalletConfig<InjectedWalletOptions> & {
  /** @description Name to show when no wallet is detected */
  genericName?: string;
  /** @description Icon to show when no wallet is detected */
  genericIcon?: string;
  /** @description Url to show when no wallet is detected */
  genericUrl?: string;
  /** @description Show as a generic wallet regardless of what extension is installed or detected */
  showAsGeneric?: boolean;
};

export class InjectedWallet extends EVMWallet<
  InjectedConnector,
  InjectedWalletOptions
> {
  private config: InjectedWalletConfig;

  constructor(config: InjectedWalletConfig = {}) {
    super(config);
    this.config = config;
  }

  createConnector(): InjectedConnector {
    return new InjectedConnector({
      chains: this.chains,
      options: this.connectorOptions,
    });
  }

  getName(): string {
    const name = this.connector.name;
    return name === "Injected" || this.config.showAsGeneric
      ? this.config.genericName || InjectedWallets.Generic
      : name;
  }

  getUrl(): string {
    if (this.config.showAsGeneric) {
      return this.config.genericUrl || "https://ethereum.org/";
    }

    switch (this.getName()) {
      case InjectedWallets.MetaMask:
        return "https://metamask.io/";
      case InjectedWallets.BraveWallet:
        return "https://brave.com/";
      case InjectedWallets.KuCoinWallet:
        return "https://kuwallet.com/";
      case InjectedWallets.FrontierWallet:
        return "https://www.frontier.xyz/";
      case InjectedWallets.BitKeepWallet:
        return "https://bitkeep.com/";
      case InjectedWallets.OKXWallet:
        return "https://www.okx.com/web3/";
      default:
        return "https://ethereum.org/";
    }
  }

  getIcon(): string {
    if (this.config.showAsGeneric) {
      return this.config.genericIcon || GENERIC_ICON;
    }

    switch (this.getName()) {
      case InjectedWallets.MetaMask:
        return "data:image/svg+xml;base64,";
      case InjectedWallets.BraveWallet:
        return "data:image/svg+xml;base64,";
      case InjectedWallets.KuCoinWallet:
        return "data:image/png;base64,";
      case InjectedWallets.FrontierWallet:
        return "data:image/svg+xml;base64,";
      case InjectedWallets.BitKeepWallet:
        return "data:image/svg+xml;base64,";
      case InjectedWallets.OKXWallet:
        return "data:image/png;base64,";
      default:
        return this.config.genericIcon || GENERIC_ICON;
    }
  }

  static getWalletType(): EVMWalletType {
    return EVMWalletType.Injected;
  }
}
