import { Connector } from "@wagmi/core";
import { EVMWallet, EVMWalletType } from "./evm";

export abstract class BaseWalletConnectWallet<
  C extends Connector,
  COpts
> extends EVMWallet<C, COpts> {
  getName(): string {
    return "WalletConnect";
  }

  getUrl(): string {
    return "https://walletconnect.org";
  }

  getIcon(): string {
    return "data:image/svg+xml;base64,";
  }

  static getWalletType(): EVMWalletType {
    return EVMWalletType.WalletConnect;
  }
}
